pragma solidity ^0.4.22;

import "openzeppelin-solidity/contracts/token/ERC20/StandardToken.sol";

contract HydraExchangeFactory {
    function create_contract_eth_token(ERC20 token, uint amount_eth, uint amount_token, address recipient_eth, address recipient_token) public returns (address) {
        address cont=new HydraExchangeEthToken(token,amount_eth,amount_token,recipient_eth,recipient_token);
        return cont;
    }
}

contract HydraExchangeEthToken {
	ERC20 token;
    uint amount_eth;
    uint amount_token;
    address recipient_eth;
    address recipient_token;

    constructor(ERC20 _token,uint _amount_eth,uint _amount_token,address _recipient_eth,address _recipient_token) public {
		token=_token;
        amount_eth=_amount_eth;
        amount_token=_amount_token;
        recipient_eth=_recipient_eth;
        recipient_token=_recipient_token;
    }

    function get_deposit_amount_eth() public view returns (uint) {
        return address(this).balance;
    }

    function get_deposit_amount_token() public view returns (uint) {
        return token.balanceOf(address(this));
    }

    function release_funds() public {
        uint deposit_amount_eth=get_deposit_amount_eth();
        uint deposit_amount_token=get_deposit_amount_token();
        assert(deposit_amount_eth>=amount_eth);
        assert(deposit_amount_token>=amount_token);
        recipient_eth.transfer(amount_eth);
        token.transfer(recipient_token,amount_token);
    }
}
